function Drop(canvas){
	this.canvas = canvas;
	this.ctx = canvas.getContext("2d");
	this.canvasWidth = this.canvas.width;
	this.canvasHeight = this.canvas.height;
	this.rebounding = false;
	this.overbounding = false;
	//触发刷新时d的最大值，阈值
	this.overboundD = 1.1;
	this.time =0;
	this.useTime = false;
	//按钮被下拉距离，取值(0-1),大于1.1的时候触发加载
	//按钮颜色
	this.color = "#909090";
	//按钮阴影色
	this.shadowColor = "#595959";
	//加载中图标
	this.icon_load_src = "data:image/png;base64,";
	this.icon_load = document.createElement("img");
	this.icon_load.src = this.icon_load_src;
	//刷新箭头
	this.icon_arr_src = "data:image/png;base64,";
	this.icon_arr = document.createElement("img");
	this.icon_arr.src = this.icon_arr_src;
	this.startd = 0;

	this.d = 0;
	this.c1 = {
		x:100,
		y:100,
		r:50-this.d/3
	};
	this.c2 = {
		x:this.c1.x,
		y:this.c1.y+this.d,
		r:50-this.d/2
	};
	this.calc();
}
Drop.prototype.calc = function(){
	this.c1 = {
		x:100,
		y:100,
		r:50-this.d/3
	};
	this.c2 = {
		x:this.c1.x,
		y:this.c1.y+this.d,
		r:50-this.d/2
	};
	this.p1 = {
		x:this.c1.x+this.c1.r,
		y:this.c1.y
	};
	this.p2 = {
		x:this.c2.x+this.c2.r,
		y:this.c2.y
	};
	this.p3 = {
		x:this.c2.x - this.c2.r,
		y:this.c2.y
	};
	this.p4 = {
		x:this.c1.x-this.c1.r,
		y:this.c1.y
	};
	this.cp1 = {
		x:this.p2.x,
		y:this.c1.y+this.d/2
	};
	this.cp2 = {
		x:this.p3.x,
		y:this.c1.y+this.d/2
	};
} 
Drop.prototype.draw = function(time){
	//如果传入当前时间戳，则按照时间戳计时否则用time累加计时
	if(time === undefined){
		this.time++;
		this.useTime = false;
	}else{
		this.time = time;
		this.useTime = true;
	}
	//做回弹动画，根据回弹用时计算出拉动距离d
	/*if(this.rebounding){
		if(this.d >0){
			//回弹时的时间函数,取自tween.js  Exponential.Out
			function timing(t, b, c, d) {
	            return (t==d) ? b + c : c * (-Math.pow(2, -10 * t/d) + 1) + b;
	        }
	        var toTime = this.useTime?1500:80;
	        this.d = timing(this.time-this.reboundTime,this.reboundD,-this.reboundD,toTime);
			this.d = this.d<0.01?0:this.d;
			this.startd = this.d;
		}else{
			this.rebounding = false;
		} 
	}*/
	//根据最新的d重新计算点位
	this.calc();

	//开始绘制
	this.ctx.save();
	this.ctx.fillStyle = this.color;
	//绘制阴影
	//this.ctx.shadowBlur=2;
	//this.ctx.shadowOffsetX=2;
	//this.ctx.shadowColor=this.shadowColor;

	this.ctx.beginPath();
	//绘制上圆
	this.ctx.arc(this.c1.x, this.c1.y, this.c1.r, 0, 2 * Math.PI);
	this.ctx.fill();

	//绘制曲线
	this.ctx.moveTo(this.p4.x,this.p4.y);
	this.ctx.lineTo(this.p1.x,this.p1.y)
	this.ctx.quadraticCurveTo(this.cp1.x,this.cp1.y,this.p2.x,this.p2.y);
	this.ctx.lineTo(this.p3.x,this.p3.y);
	this.ctx.quadraticCurveTo(this.cp2.x,this.cp2.y,this.p4.x,this.p4.y);
	this.ctx.fill();

	//绘制下圆
	this.ctx.arc(this.c2.x, this.c2.y, this.c2.r, 0, 2 * Math.PI);
	this.ctx.fill();
	this.ctx.closePath();
	this.ctx.restore();
	
	/*//绘制上圆中的图标
	this.ctx.save();
	//将坐标移至中心，并旋转
	this.ctx.translate(this.c1.x,this.c1.y);
	//确定图标样式
	var tmpIcon = this.overbounding?this.icon_load:this.icon_arr;
	//确定旋转角度
	var tmpDtime = this.useTime?(this.time-this.reboundTime)/10:(this.time-this.reboundTime);
	var tmpRotate = this.overbounding?tmpDtime*(Math.PI/180):this.d*360*Math.PI/180;
	this.ctx.rotate(tmpRotate);
	this.ctx.drawImage(tmpIcon,-this.c1.r/2,-this.c1.r/2,this.c1.r,this.c1.r);
	this.ctx.restore();*/
}
